# requirements.R

# Nutze das benutzerdefinierte Library-Verzeichnis, falls gesetzt
if (!is.na(Sys.getenv("R_LIBS_USER", unset = NA))) {
  .libPaths(Sys.getenv("R_LIBS_USER"))
}

install_if_missing &lt;- function(pkg) {
  if (!pkg %in% rownames(installed.packages())) {
    install.packages(pkg, repos = "https://cloud.r-project.org")
  }
}

# Liste der benötigten Pakete – hier kannst du jederzeit erweitern
pkgs &lt;- c(
  "tidyverse",
  "knitr",
  "rmarkdown"
)

sapply(pkgs, install_if_missing)

Kombination select() und full_join() - Beispiel

penguins &lt;- palmerpenguins::penguins
penguins &lt;- penguins |&gt;
  mutate(id = row_number())

penguins2 &lt;- palmerpenguins::penguins
penguins2 &lt;- penguins2 |&gt;
  mutate(id = row_number())


dat_final &lt;- penguins |&gt;
  select(id, species, year) |&gt;
  full_join(
    penguins2 |&gt; select(id, island, sex),
    by = "id"
  )
